module Wasp.Generator.Common
  ( ProjectRootDir,
    nodeVersionRange,
    npmVersionRange,
    prismaVersion,
  )
where

import qualified Wasp.SemanticVersion as V

-- | Directory where the whole web app project (client, server, ...) is generated.
data ProjectRootDir

-- | Range of node versions that node packages generated by this generator work correctly with.
nodeVersionRange :: V.Range
nodeVersionRange =
  V.Range [V.caret latestLTSVersion <> V.lte latestLTSExactVersionThatWeKnowWorks]
  where
    latestLTSVersion = V.Version 16 0 0
    -- There is a bug in node 16.15.1 (more correctly, in npm 8.11.0 that comes with it)
    -- that messes up how Wasp uses Prisma. That is why we limited ourselves to
    -- <=16.15.0 for now.
    -- Bug issue on NPM CLI repo: https://github.com/npm/cli/issues/5018 .
    latestLTSExactVersionThatWeKnowWorks = V.Version 16 15 0

-- | Range of npm versions that Wasp and generated projects work correctly with.
npmVersionRange :: V.Range
npmVersionRange =
  V.Range [V.caret latestLTSVersion <> V.lte latestLTSExactVersionThatWeKnowWorks]
  where
    latestLTSVersion = V.Version 8 0 0 -- Goes with node 16
    latestLTSExactVersionThatWeKnowWorks = V.Version 8 5 5 -- Goes with node 16.15.0

prismaVersion :: V.Version
prismaVersion = V.Version 3 9 1
